import React, { useState } from 'react';
import axios from 'axios';
import { FaRobot, FaSpinner, FaInfoCircle, FaCheck } from 'react-icons/fa';

const GeminiTest = () => {
  const [prompt, setPrompt] = useState('');
  const [contentType, setContentType] = useState('blog');
  const [advanced, setAdvanced] = useState(false);
  const [temperature, setTemperature] = useState(0.7);
  const [maxTokens, setMaxTokens] = useState(2048);
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [copied, setCopied] = useState(false);

  const contentTypes = [
    { value: 'blog', label: 'Blog Post' },
    { value: 'article', label: 'Article' },
    { value: 'social', label: 'Social Media' },
    { value: 'seo', label: 'SEO Content' },
    { value: 'email', label: 'Email' },
    { value: 'other', label: 'Other' }
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setResult(null);
    
    if (!prompt.trim()) {
      setError('Please enter a prompt');
      return;
    }

    try {
      setLoading(true);
      
      // Get user token from localStorage
      const user = JSON.parse(localStorage.getItem('user'));
      
      if (!user || !user.token) {
        throw new Error('You need to be logged in to test the API');
      }

      // Create request options
      const requestOptions = {
        prompt,
        contentType,
      };

      // Add advanced options if enabled
      if (advanced) {
        requestOptions.options = {
          temperature: parseFloat(temperature),
          maxOutputTokens: parseInt(maxTokens),
        };
      }
      
      // Make direct API request to the test endpoint
      const response = await axios.post(
        '/api/content/test-gemini',
        requestOptions,
        {
          headers: {
            'Content-Type': 'application/json',
            'x-auth-token': user.token
          }
        }
      );
      
      setResult(response.data);
    } catch (err) {
      console.error('Error testing Gemini API:', err);
      setError(err.response?.data?.msg || err.message || 'An unknown error occurred');
    } finally {
      setLoading(false);
    }
  };

  const handleCopy = () => {
    if (result?.generatedContent) {
      navigator.clipboard.writeText(result.generatedContent);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    }
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <div className="flex items-center mb-6">
        <FaRobot className="text-3xl text-primary-600 mr-3" />
        <h1 className="text-3xl font-bold text-gray-800">Gemini API Test Tool</h1>
      </div>
      
      <div className="bg-blue-50 border-l-4 border-blue-400 p-4 mb-6">
        <div className="flex">
          <div className="flex-shrink-0">
            <FaInfoCircle className="h-5 w-5 text-blue-400" />
          </div>
          <div className="ml-3">
            <p className="text-sm text-blue-700">
              This tool lets you test the Google Gemini API directly. The generated content will not be saved to your history or count towards your usage limits.
            </p>
          </div>
        </div>
      </div>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-bold text-gray-800 mb-4">Test Parameters</h2>
          
          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="contentType">
                Content Type
              </label>
              <select
                id="contentType"
                value={contentType}
                onChange={(e) => setContentType(e.target.value)}
                className="w-full p-2 border border-gray-300 rounded-md focus:ring-primary-500 focus:border-primary-500"
              >
                {contentTypes.map(type => (
                  <option key={type.value} value={type.value}>
                    {type.label}
                  </option>
                ))}
              </select>
            </div>
            
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="prompt">
                Prompt
              </label>
              <textarea
                id="prompt"
                value={prompt}
                onChange={(e) => setPrompt(e.target.value)}
                placeholder="Describe what content you want to generate..."
                className="w-full p-2 border border-gray-300 rounded-md focus:ring-primary-500 focus:border-primary-500"
                rows="5"
                required
              ></textarea>
            </div>
            
            <div className="mb-6">
              <div className="flex items-center justify-between mb-2">
                <button
                  type="button"
                  onClick={() => setAdvanced(!advanced)}
                  className="text-sm text-primary-600 hover:text-primary-800 flex items-center"
                >
                  {advanced ? 'Hide' : 'Show'} Advanced Options
                </button>
              </div>
              
              {advanced && (
                <div className="bg-gray-50 p-3 rounded-md border border-gray-200">
                  <div className="mb-3">
                    <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="temperature">
                      Temperature: {temperature}
                    </label>
                    <input
                      id="temperature"
                      type="range"
                      min="0"
                      max="1"
                      step="0.1"
                      value={temperature}
                      onChange={(e) => setTemperature(e.target.value)}
                      className="w-full"
                    />
                    <div className="flex justify-between text-xs text-gray-500">
                      <span>More Focused</span>
                      <span>More Creative</span>
                    </div>
                  </div>
                  
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="maxTokens">
                      Max Output Tokens: {maxTokens}
                    </label>
                    <input
                      id="maxTokens"
                      type="range"
                      min="256"
                      max="4096"
                      step="256"
                      value={maxTokens}
                      onChange={(e) => setMaxTokens(e.target.value)}
                      className="w-full"
                    />
                    <div className="flex justify-between text-xs text-gray-500">
                      <span>Shorter</span>
                      <span>Longer</span>
                    </div>
                  </div>
                </div>
              )}
            </div>
            
            <button
              type="submit"
              disabled={loading}
              className="w-full bg-primary-600 text-white py-2 px-4 rounded-md hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-opacity-50 disabled:bg-gray-400"
            >
              {loading ? (
                <>
                  <FaSpinner className="inline animate-spin mr-2" />
                  Testing API...
                </>
              ) : (
                'Test Gemini API'
              )}
            </button>
          </form>
        </div>
        
        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="flex justify-between mb-4">
            <h2 className="text-xl font-bold text-gray-800">API Response</h2>
            
            {result && (
              <button 
                onClick={handleCopy} 
                className="text-sm text-primary-600 hover:text-primary-800 flex items-center"
              >
                {copied ? <FaCheck className="mr-1" /> : <FaRobot className="mr-1" />}
                {copied ? 'Copied!' : 'Copy Content'}
              </button>
            )}
          </div>
          
          {error && (
            <div className="bg-red-50 border border-red-200 text-red-800 rounded-lg p-3 mb-4">
              {error}
            </div>
          )}
          
          <div className="border border-gray-200 rounded-lg p-4 h-[400px] overflow-y-auto bg-gray-50">
            {loading ? (
              <div className="h-full flex flex-col items-center justify-center text-gray-400">
                <FaSpinner className="animate-spin text-2xl mb-2" />
                <p>Testing Gemini API...</p>
              </div>
            ) : result ? (
              <div>
                <div className="whitespace-pre-wrap mb-4">{result.generatedContent}</div>
                <div className="mt-4 pt-4 border-t border-gray-200 text-xs text-gray-500">
                  <div>Content Type: <span className="font-medium">{result.contentType}</span></div>
                  <div>Length: <span className="font-medium">{result.length} characters</span></div>
                  <div>Generated at: <span className="font-medium">{new Date(result.timestamp).toLocaleString()}</span></div>
                </div>
              </div>
            ) : (
              <div className="h-full flex items-center justify-center text-gray-400">
                Test results will appear here
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default GeminiTest;
