import React, { useState } from 'react';
import GeminiAI from '../components/GeminiAI';
import { FaCopy, FaDownload, FaCheck } from 'react-icons/fa';

const GeminiDemo = () => {
  const [generatedContent, setGeneratedContent] = useState('');
  const [copied, setCopied] = useState(false);

  const handleContentGenerated = (content) => {
    setGeneratedContent(content);
    setCopied(false);
  };

  const handleCopyContent = () => {
    navigator.clipboard.writeText(generatedContent);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  const handleDownloadContent = () => {
    const element = document.createElement('a');
    const file = new Blob([generatedContent], { type: 'text/plain' });
    element.href = URL.createObjectURL(file);
    element.download = 'generated-content.txt';
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-gray-800 mb-8">Google Gemini AI Demo</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        <GeminiAI onContentGenerated={handleContentGenerated} />
        
        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-bold text-gray-800">Generated Content</h2>
            
            {generatedContent && (
              <div className="flex space-x-2">
                <button 
                  onClick={handleCopyContent}
                  className="text-gray-600 hover:text-primary-600 flex items-center px-2 py-1 text-sm rounded-md hover:bg-gray-100"
                  title="Copy to clipboard"
                >
                  {copied ? <FaCheck className="mr-1" /> : <FaCopy className="mr-1" />}
                  {copied ? 'Copied!' : 'Copy'}
                </button>
                
                <button 
                  onClick={handleDownloadContent}
                  className="text-gray-600 hover:text-primary-600 flex items-center px-2 py-1 text-sm rounded-md hover:bg-gray-100"
                  title="Download as text file"
                >
                  <FaDownload className="mr-1" />
                  Download
                </button>
              </div>
            )}
          </div>
          
          <div className="border border-gray-200 rounded-lg p-4 h-96 overflow-y-auto bg-gray-50">
            {generatedContent ? (
              <div className="whitespace-pre-wrap">{generatedContent}</div>
            ) : (
              <div className="h-full flex items-center justify-center text-gray-400">
                Generated content will appear here
              </div>
            )}
          </div>
        </div>
      </div>
      
      <div className="mt-8 bg-white rounded-lg shadow-md p-6">
        <h2 className="text-xl font-bold text-gray-800 mb-4">About Google Gemini AI</h2>
        <p className="text-gray-600 mb-4">
          Google Gemini is a state-of-the-art large language model developed by Google AI. 
          It can generate various types of content, from blog posts and articles to social media 
          content and email templates.
        </p>
        <p className="text-gray-600">
          Simply enter a prompt describing what you want to generate, select the content type,
          and let the AI do the work. You can then copy or download the generated content for your use.
        </p>
      </div>
    </div>
  );
};

export default GeminiDemo;
