import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { 
  PayPalScriptProvider, 
  PayPalButtons,
  usePayPalScriptReducer
} from '@paypal/react-paypal-js';
import { 
  createPayPalOrder, 
  capturePayPalOrder, 
  clearPaymentStatus 
} from '../features/subscription/subscriptionSlice';
import { FaSpinner, FaExclamationTriangle, FaCheckCircle, FaPaypal } from 'react-icons/fa';

// Inner component to handle PayPal script loading status
const PayPalButtonWrapper = ({ createOrder, onApprove, onCancel, onError }) => {
  const [{ isPending, isRejected }] = usePayPalScriptReducer();
  
  if (isPending) {
    return (
      <div className="flex items-center justify-center py-4">
        <FaSpinner className="animate-spin text-xl text-blue-500 mr-2" />
        <span className="text-gray-600">Loading PayPal...</span>
      </div>
    );
  }
  
  if (isRejected) {
    return (
      <div className="text-center py-4">
        <FaExclamationTriangle className="text-2xl text-red-500 mx-auto mb-2" />
        <p className="text-red-600 font-medium">Failed to load PayPal</p>
        <p className="text-sm text-gray-600 mt-1">
          Please try again later or contact support
        </p>
      </div>
    );
  }
  
  return (
    <PayPalButtons
      style={{
        layout: 'vertical',
        color: 'blue',
        shape: 'rect',
        label: 'pay',
        height: 45
      }}
      fundingSource="paypal"
      createOrder={createOrder}
      onApprove={onApprove}
      onCancel={onCancel}
      onError={onError}
    />
  );
};

const PayPalButton = ({ planId, onSuccess, redirectOnSuccess = false }) => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { paymentStatus, isLoading, isError, message } = useSelector(
    (state) => state.subscription
  );
  const [hasClicked, setHasClicked] = useState(false);
  const [paymentSuccessful, setPaymentSuccessful] = useState(false);
  const [paypalError, setPaypalError] = useState(null);

  useEffect(() => {
    // Clear any previous payment status when component mounts
    dispatch(clearPaymentStatus());
    return () => {
      // Clean up on unmount
      dispatch(clearPaymentStatus());
    };
  }, [dispatch]);

  useEffect(() => {
    // Handle successful payment
    if (paymentStatus === 'COMPLETED') {
      setPaymentSuccessful(true);
      
      if (redirectOnSuccess) {
        // Redirect to success page if option is enabled
        navigate('/payment/success');
      } else if (onSuccess) {
        // Otherwise call the onSuccess callback
        onSuccess(planId);
      }
    }
  }, [paymentStatus, planId, onSuccess, navigate, redirectOnSuccess]);

  const handleCreateOrder = async () => {
    try {
      setHasClicked(true);
      setPaypalError(null);
      const actionResult = await dispatch(createPayPalOrder(planId));
      if (createPayPalOrder.fulfilled.match(actionResult)) {
        return actionResult.payload.orderId;
      } else {
        throw new Error(actionResult.payload || 'Error creating order');
      }
    } catch (error) {
      console.error('Error creating order:', error);
      setPaypalError(error.message || 'Failed to create PayPal order');
      // Return nothing if there's an error, which will abort the PayPal flow
    }
  };

  const handleApprove = async (data) => {
    try {
      await dispatch(capturePayPalOrder(data.orderID));
    } catch (error) {
      console.error('Error capturing order:', error);
      setPaypalError(error.message || 'Failed to process payment');
    }
  };

  const handleCancel = () => {
    setHasClicked(false);
    if (redirectOnSuccess) {
      navigate('/payment/cancelled');
    }
  };

  const handleError = (err) => {
    console.error('PayPal error:', err);
    setPaypalError('There was a problem with PayPal. Please try again.');
  };

  // PayPal client configuration
  const paypalOptions = {
    "client-id": "sb", // Hardcoded for sandbox testing
    currency: "USD",
    intent: "capture",
    components: "buttons",
  };

  if (isLoading) {
    return (
      <div className="py-6 px-4 bg-gray-50 rounded-lg text-center">
        <FaSpinner className="animate-spin text-3xl text-primary-500 mx-auto mb-2" />
        <p className="text-gray-700 font-medium">Processing payment...</p>
        <p className="text-sm text-gray-500 mt-1">Please wait while we set up your payment</p>
      </div>
    );
  }

  if (paymentSuccessful) {
    return (
      <div className="py-6 px-4 bg-green-50 text-green-800 rounded-lg text-center">
        <FaCheckCircle className="text-3xl text-green-500 mx-auto mb-2" />
        <p className="font-medium">Payment successful!</p>
        <p className="text-sm mt-1">Your subscription has been updated.</p>
      </div>
    );
  }

  return (
    <div className="w-full">
      {(isError || paypalError) && (
        <div className="bg-red-50 text-red-800 p-4 rounded-lg mb-4 flex items-start">
          <FaExclamationTriangle className="text-red-500 mt-1 mr-2 flex-shrink-0" />
          <div>
            <p className="font-medium">There was an error with your payment</p>
            <p className="text-sm mt-1">{message || paypalError || "Please try again or contact support."}</p>
          </div>
        </div>
      )}
      
      {!hasClicked && (
        <button
          onClick={() => setHasClicked(true)}
          className="w-full py-3 mb-4 bg-blue-600 hover:bg-blue-700 text-white rounded-lg flex items-center justify-center font-medium transition-colors"
        >
          <FaPaypal className="mr-2" /> Pay with PayPal
        </button>
      )}
      
      {hasClicked && (
        <div className="border border-gray-200 rounded-lg p-4 bg-gray-50">
          <PayPalScriptProvider options={paypalOptions} deferLoading={false}>
            <PayPalButtonWrapper
              createOrder={handleCreateOrder}
              onApprove={handleApprove}
              onCancel={handleCancel}
              onError={handleError}
            />
          </PayPalScriptProvider>
        </div>
      )}
      
      <div className="mt-4 text-center">
        <button
          onClick={() => setHasClicked(false)}
          className="text-gray-600 hover:text-gray-800 text-sm font-medium"
        >
          Cancel
        </button>
      </div>
      
      <div className="mt-4 border-t border-gray-200 pt-4">
        <p className="text-xs text-gray-500 text-center">
          Your payment is secure and processed by PayPal. You can cancel your subscription at any time from your account settings.
        </p>
      </div>
    </div>
  );
};

export default PayPalButton;
