import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import { FaUser, FaFileAlt, FaCrown } from 'react-icons/fa';
import { getContents } from '../features/content/contentSlice';

const Dashboard = () => {
  const dispatch = useDispatch();
  const { user } = useSelector((state) => state.auth);
  const { contents } = useSelector((state) => state.content);

  useEffect(() => {
    if (user) {
      dispatch(getContents({ limit: 5 }));
    }
  }, [user, dispatch]);

  if (!user) {
    return <div>Loading user data...</div>;
  }

  const { name, avatar, subscription, usageCount, usageLimit, usagePercentage } = user;

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <div className="flex items-center mb-6">
        <img
          src={avatar}
          alt={name}
          className="w-16 h-16 rounded-full mr-4 border-2 border-primary-300"
        />
        <div>
          <h2 className="text-2xl font-semibold text-gray-800">{name}</h2>
          <div className="flex items-center gap-2 mt-1">
            <span
              className={`inline-flex items-center rounded-full px-3 py-1 text-xs font-medium ${
                subscription.currentPlan === 'free'
                  ? 'bg-gray-100 text-gray-800'
                  : subscription.currentPlan === 'basic'
                  ? 'bg-primary-100 text-primary-800'
                  : 'bg-secondary-100 text-secondary-800'
              }`}
            >
              <FaCrown className="mr-1" />
              {subscription.currentPlan.charAt(0).toUpperCase() + subscription.currentPlan.slice(1)} Plan
            </span>
            {subscription.daysRemaining !== null && (
              <span className="text-sm text-gray-500">
                {subscription.daysRemaining} days remaining
              </span>
            )}
          </div>
        </div>
      </div>

      <div className="mb-6">
        <div className="flex justify-between mb-2">
          <span className="text-sm font-medium text-gray-700">
            Usage ({usageCount}/{usageLimit})
          </span>
          <span className="text-sm font-medium text-gray-700">{usagePercentage}%</span>
        </div>
        <div className="w-full bg-gray-200 rounded-full h-2.5">
          <div
            className={`h-2.5 rounded-full ${
              usagePercentage < 70
                ? 'bg-green-500'
                : usagePercentage < 90
                ? 'bg-yellow-500'
                : 'bg-red-500'
            }`}
            style={{ width: `${usagePercentage}%` }}
          ></div>
        </div>
      </div>

      <div className="mb-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-gray-800">Recent Content</h3>
          <Link to="/content" className="text-sm text-primary-600 hover:text-primary-800">
            View All
          </Link>
        </div>
        {contents.length === 0 ? (
          <div className="text-gray-500 text-center py-4">
            No content yet. Start generating!
          </div>
        ) : (
          <div className="space-y-3">
            {contents.slice(0, 5).map((content) => (
              <Link
                to={`/content/${content._id}`}
                key={content._id}
                className="block p-3 bg-gray-50 rounded-lg hover:bg-gray-100"
              >
                <div className="flex items-start">
                  <FaFileAlt className="text-gray-400 mr-3 mt-1" />
                  <div>
                    <p className="font-medium text-gray-800 line-clamp-1">
                      {content.prompt}
                    </p>
                    <div className="flex items-center mt-1">
                      <span className="text-xs text-gray-500 capitalize">
                        {content.contentType}
                      </span>
                      <span className="mx-2 text-gray-300">•</span>
                      <span className="text-xs text-gray-500">
                        {new Date(content.createdAt).toLocaleDateString()}
                      </span>
                    </div>
                  </div>
                </div>
              </Link>
            ))}
          </div>
        )}
      </div>

      <div className="flex gap-4">
        <Link
          to="/generator"
          className="flex-1 bg-primary-600 text-white text-center py-2 px-4 rounded-lg hover:bg-primary-700 transition-colors"
        >
          Generate Content
        </Link>
        <Link
          to="/subscription"
          className="flex-1 bg-white text-primary-600 text-center py-2 px-4 rounded-lg border border-primary-600 hover:bg-primary-50 transition-colors"
        >
          Manage Subscription
        </Link>
      </div>
    </div>
  );
};

export default Dashboard;
