import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api/payments';

const initialState = {
  plans: [],
  userSubscription: null,
  orderId: null,
  paymentStatus: null,
  isError: false,
  isSuccess: false,
  isLoading: false,
  message: '',
};

// Get subscription plans
export const getSubscriptionPlans = createAsyncThunk(
  'subscription/getPlans',
  async (_, thunkAPI) => {
    try {
      const response = await axios.get(`${API_URL}/plans`);
      return response.data;
    } catch (error) {
      const message =
        (error.response &&
          error.response.data &&
          error.response.data.msg) ||
        error.message ||
        error.toString();
      return thunkAPI.rejectWithValue(message);
    }
  }
);

// Create PayPal order
export const createPayPalOrder = createAsyncThunk(
  'subscription/createOrder',
  async (planId, thunkAPI) => {
    try {
      const token = thunkAPI.getState().auth.user.token;

      const config = {
        headers: {
          'x-auth-token': token,
        },
      };

      const response = await axios.post(
        `${API_URL}/create-order`,
        { planId },
        config
      );
      
      return response.data;
    } catch (error) {
      const message =
        (error.response &&
          error.response.data &&
          error.response.data.msg) ||
        error.message ||
        error.toString();
      return thunkAPI.rejectWithValue(message);
    }
  }
);

// Capture PayPal order
export const capturePayPalOrder = createAsyncThunk(
  'subscription/captureOrder',
  async (orderId, thunkAPI) => {
    try {
      const token = thunkAPI.getState().auth.user.token;

      const config = {
        headers: {
          'x-auth-token': token,
        },
      };

      const response = await axios.post(
        `${API_URL}/capture-order`,
        { orderId },
        config
      );
      
      return response.data;
    } catch (error) {
      const message =
        (error.response &&
          error.response.data &&
          error.response.data.msg) ||
        error.message ||
        error.toString();
      return thunkAPI.rejectWithValue(message);
    }
  }
);

// Get user subscription details
export const getUserSubscription = createAsyncThunk(
  'subscription/getUserSubscription',
  async (_, thunkAPI) => {
    try {
      const token = thunkAPI.getState().auth.user.token;

      const config = {
        headers: {
          'x-auth-token': token,
        },
      };

      const response = await axios.get(`${API_URL}/subscription`, config);
      return response.data;
    } catch (error) {
      const message =
        (error.response &&
          error.response.data &&
          error.response.data.msg) ||
        error.message ||
        error.toString();
      return thunkAPI.rejectWithValue(message);
    }
  }
);

export const subscriptionSlice = createSlice({
  name: 'subscription',
  initialState,
  reducers: {
    reset: (state) => {
      state.isLoading = false;
      state.isSuccess = false;
      state.isError = false;
      state.message = '';
    },
    clearPaymentStatus: (state) => {
      state.paymentStatus = null;
      state.orderId = null;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(getSubscriptionPlans.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(getSubscriptionPlans.fulfilled, (state, action) => {
        state.isLoading = false;
        state.isSuccess = true;
        state.plans = action.payload;
      })
      .addCase(getSubscriptionPlans.rejected, (state, action) => {
        state.isLoading = false;
        state.isError = true;
        state.message = action.payload;
      })
      .addCase(createPayPalOrder.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(createPayPalOrder.fulfilled, (state, action) => {
        state.isLoading = false;
        state.isSuccess = true;
        state.orderId = action.payload.orderId;
      })
      .addCase(createPayPalOrder.rejected, (state, action) => {
        state.isLoading = false;
        state.isError = true;
        state.message = action.payload;
      })
      .addCase(capturePayPalOrder.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(capturePayPalOrder.fulfilled, (state, action) => {
        state.isLoading = false;
        state.isSuccess = true;
        state.paymentStatus = action.payload.status;
        // Update subscription details after successful payment
        if (action.payload.success) {
          state.userSubscription = {
            status: action.payload.planId,
            isActive: true
          };
        }
      })
      .addCase(capturePayPalOrder.rejected, (state, action) => {
        state.isLoading = false;
        state.isError = true;
        state.message = action.payload;
      })
      .addCase(getUserSubscription.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(getUserSubscription.fulfilled, (state, action) => {
        state.isLoading = false;
        state.isSuccess = true;
        state.userSubscription = action.payload.subscription;
      })
      .addCase(getUserSubscription.rejected, (state, action) => {
        state.isLoading = false;
        state.isError = true;
        state.message = action.payload;
      });
  },
});

export const { reset, clearPaymentStatus } = subscriptionSlice.actions;
export default subscriptionSlice.reducer;
