import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api/content';

const initialState = {
  contents: [],
  currentContent: null,
  pagination: {
    page: 1,
    limit: 10,
    total: 0,
    pages: 0,
  },
  isError: false,
  isSuccess: false,
  isLoading: false,
  message: '',
  limitReached: false,
};

// Generate content
export const generateContent = createAsyncThunk(
  'content/generate',
  async (contentData, thunkAPI) => {
    try {
      const token = thunkAPI.getState().auth.user.token;

      const config = {
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': token,
        },
      };

      const response = await axios.post(`${API_URL}/generate`, contentData, config);
      return response.data;
    } catch (error) {
      const message =
        (error.response &&
          error.response.data &&
          error.response.data.msg) ||
        error.message ||
        error.toString();

      // Check if limit is reached
      if (error.response && error.response.data && error.response.data.limitReached) {
        return thunkAPI.rejectWithValue({ 
          message, 
          limitReached: true 
        });
      }

      // Check if subscription has expired
      if (error.response && error.response.data && error.response.data.subscriptionExpired) {
        return thunkAPI.rejectWithValue({ 
          message, 
          subscriptionExpired: true 
        });
      }

      return thunkAPI.rejectWithValue({ message });
    }
  }
);

// Get user contents with pagination
export const getContents = createAsyncThunk(
  'content/getAll',
  async (params, thunkAPI) => {
    try {
      const token = thunkAPI.getState().auth.user.token;
      const { page = 1, limit = 10, contentType = 'all' } = params || {};

      const config = {
        headers: {
          'x-auth-token': token,
        },
        params: {
          page,
          limit,
          contentType,
        },
      };

      const response = await axios.get(API_URL, config);
      return response.data;
    } catch (error) {
      const message =
        (error.response &&
          error.response.data &&
          error.response.data.msg) ||
        error.message ||
        error.toString();
      return thunkAPI.rejectWithValue(message);
    }
  }
);

// Get content by ID
export const getContentById = createAsyncThunk(
  'content/getById',
  async (id, thunkAPI) => {
    try {
      const token = thunkAPI.getState().auth.user.token;

      const config = {
        headers: {
          'x-auth-token': token,
        },
      };

      const response = await axios.get(`${API_URL}/${id}`, config);
      return response.data;
    } catch (error) {
      const message =
        (error.response &&
          error.response.data &&
          error.response.data.msg) ||
        error.message ||
        error.toString();
      return thunkAPI.rejectWithValue(message);
    }
  }
);

// Delete content
export const deleteContent = createAsyncThunk(
  'content/delete',
  async (id, thunkAPI) => {
    try {
      const token = thunkAPI.getState().auth.user.token;

      const config = {
        headers: {
          'x-auth-token': token,
        },
      };

      await axios.delete(`${API_URL}/${id}`, config);
      return id;
    } catch (error) {
      const message =
        (error.response &&
          error.response.data &&
          error.response.data.msg) ||
        error.message ||
        error.toString();
      return thunkAPI.rejectWithValue(message);
    }
  }
);

export const contentSlice = createSlice({
  name: 'content',
  initialState,
  reducers: {
    reset: (state) => {
      state.isLoading = false;
      state.isSuccess = false;
      state.isError = false;
      state.message = '';
      state.limitReached = false;
    },
    clearCurrentContent: (state) => {
      state.currentContent = null;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(generateContent.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(generateContent.fulfilled, (state, action) => {
        state.isLoading = false;
        state.isSuccess = true;
        state.currentContent = action.payload;
        state.contents.unshift(action.payload);
      })
      .addCase(generateContent.rejected, (state, action) => {
        state.isLoading = false;
        state.isError = true;
        state.message = action.payload.message;
        state.limitReached = action.payload.limitReached || false;
      })
      .addCase(getContents.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(getContents.fulfilled, (state, action) => {
        state.isLoading = false;
        state.isSuccess = true;
        state.contents = action.payload.content;
        state.pagination = action.payload.pagination;
      })
      .addCase(getContents.rejected, (state, action) => {
        state.isLoading = false;
        state.isError = true;
        state.message = action.payload;
      })
      .addCase(getContentById.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(getContentById.fulfilled, (state, action) => {
        state.isLoading = false;
        state.isSuccess = true;
        state.currentContent = action.payload;
      })
      .addCase(getContentById.rejected, (state, action) => {
        state.isLoading = false;
        state.isError = true;
        state.message = action.payload;
      })
      .addCase(deleteContent.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(deleteContent.fulfilled, (state, action) => {
        state.isLoading = false;
        state.isSuccess = true;
        state.contents = state.contents.filter(
          (content) => content._id !== action.payload
        );
      })
      .addCase(deleteContent.rejected, (state, action) => {
        state.isLoading = false;
        state.isError = true;
        state.message = action.payload;
      });
  },
});

export const { reset, clearCurrentContent } = contentSlice.actions;
export default contentSlice.reducer;
