import React, { useEffect } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { FaCheckCircle, FaSpinner } from 'react-icons/fa';
import { capturePayPalOrder, getUserSubscription } from '../features/subscription/subscriptionSlice';

const PaymentSuccess = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [searchParams] = useSearchParams();
  const orderId = searchParams.get('token'); // PayPal passes the order ID as "token" in the URL
  
  const { user } = useSelector((state) => state.auth);
  const { paymentStatus, isLoading, isError, message } = useSelector(
    (state) => state.subscription
  );

  useEffect(() => {
    // Redirect if user not logged in
    if (!user) {
      navigate('/');
      return;
    }

    // If we have an orderId in the URL and payment is not yet completed, capture the order
    if (orderId && paymentStatus !== 'COMPLETED') {
      dispatch(capturePayPalOrder(orderId));
    }
    
    // If payment is completed, fetch the updated subscription
    if (paymentStatus === 'COMPLETED') {
      dispatch(getUserSubscription());
    }
  }, [user, navigate, dispatch, orderId, paymentStatus]);

  // Auto-redirect to dashboard after 5 seconds
  useEffect(() => {
    let timer;
    if (paymentStatus === 'COMPLETED') {
      timer = setTimeout(() => {
        navigate('/dashboard');
      }, 5000);
    }
    
    return () => {
      if (timer) clearTimeout(timer);
    };
  }, [paymentStatus, navigate]);

  if (isLoading) {
    return (
      <div className="max-w-4xl mx-auto px-4 py-16 text-center">
        <FaSpinner className="text-4xl text-primary-500 animate-spin mx-auto mb-6" />
        <h1 className="text-xl font-semibold text-gray-800">Processing your payment...</h1>
        <p className="text-gray-600 mt-2">Please wait while we confirm your payment with PayPal.</p>
      </div>
    );
  }

  if (isError) {
    return (
      <div className="max-w-4xl mx-auto px-4 py-16 text-center">
        <div className="bg-red-50 border border-red-200 text-red-800 rounded-lg p-6 mb-6 inline-block">
          <h1 className="text-2xl font-bold mb-4">Payment Error</h1>
          <p className="mb-4">{message || 'There was a problem processing your payment.'}</p>
          <button
            onClick={() => navigate('/subscription')}
            className="bg-primary-600 text-white py-2 px-6 rounded-lg hover:bg-primary-700"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto px-4 py-16 text-center">
      <div className="bg-green-50 border border-green-200 text-green-800 rounded-lg p-8 inline-block">
        <FaCheckCircle className="text-5xl text-green-500 mx-auto mb-6" />
        <h1 className="text-3xl font-bold mb-4">Payment Successful!</h1>
        <p className="text-xl mb-6">Thank you for your subscription.</p>
        <p className="mb-8">
          Your account has been updated and you now have access to additional features. 
          You'll be redirected to your dashboard in a few seconds.
        </p>
        <button
          onClick={() => navigate('/dashboard')}
          className="bg-primary-600 text-white py-3 px-8 rounded-lg hover:bg-primary-700"
        >
          Go to Dashboard
        </button>
      </div>
    </div>
  );
};

export default PaymentSuccess;
