import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { Link, useLocation } from 'react-router-dom';
import { FaBrain, FaLightbulb, FaRocket, FaWrench } from 'react-icons/fa';
import LoginButton from '../components/LoginButton';

const Home = () => {
  const { user } = useSelector((state) => state.auth);
  const [authError, setAuthError] = useState(false);
  const location = useLocation();

  useEffect(() => {
    // Check for auth_error in URL query params
    const queryParams = new URLSearchParams(location.search);
    if (queryParams.get('auth_error') === 'true') {
      setAuthError(true);
    }
  }, [location]);

  const features = [
    {
      icon: <FaBrain className="text-3xl text-primary-500" />,
      title: 'AI-Powered Content',
      description: 'Generate high-quality content using Google Gemini API with just a few clicks.'
    },
    {
      icon: <FaLightbulb className="text-3xl text-primary-500" />,
      title: 'Multiple Content Types',
      description: 'Generate blog posts, articles, social media content, SEO copy, email templates, and more.'
    },
    {
      icon: <FaRocket className="text-3xl text-primary-500" />,
      title: 'Subscription Plans',
      description: 'Choose from different subscription plans that fit your needs and budget.'
    },
    {
      icon: <FaWrench className="text-3xl text-primary-500" />,
      title: 'Customizable',
      description: 'Customize your generated content with different tones, styles, and formats.'
    }
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="bg-white">
        {/* Hero Section */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16 md:py-24">
          <div className="text-center">
            <h1 className="text-4xl tracking-tight font-extrabold text-gray-900 sm:text-5xl md:text-6xl">
              <span className="block">AI-Powered</span>
              <span className="block text-primary-600">Content Generator</span>
            </h1>
            <p className="mt-3 max-w-md mx-auto text-base text-gray-500 sm:text-lg md:mt-5 md:text-xl md:max-w-3xl">
              Generate high-quality content in seconds with the power of AI. Perfect for bloggers, marketers, and content creators.
            </p>
            
            {authError && (
              <div className="mt-5 max-w-md mx-auto bg-red-50 border border-red-200 text-red-800 rounded-lg p-3">
                Authentication error. Please try signing in again or contact support if the issue persists.
              </div>
            )}
            
            <div className="mt-10 flex justify-center">
              {user ? (
                <Link
                  to="/dashboard"
                  className="px-8 py-3 border border-transparent text-base font-medium rounded-md text-white bg-primary-600 hover:bg-primary-700 md:py-4 md:text-lg md:px-10"
                >
                  Go to Dashboard
                </Link>
              ) : (
                <LoginButton />
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Features Section */}
      <div className="py-12 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <h2 className="text-3xl font-extrabold tracking-tight text-gray-900 sm:text-4xl">
              Powerful Features
            </h2>
            <p className="mt-4 max-w-2xl text-xl text-gray-500 mx-auto">
              Everything you need to create amazing content with AI
            </p>
          </div>

          <div className="mt-16">
            <div className="grid grid-cols-1 gap-8 sm:grid-cols-2 lg:grid-cols-4">
              {features.map((feature, index) => (
                <div key={index} className="pt-6">
                  <div className="flow-root bg-gray-50 rounded-lg px-6 pb-8">
                    <div className="-mt-6">
                      <div>
                        <span className="inline-flex items-center justify-center p-3 bg-primary-500 rounded-md shadow-lg">
                          {feature.icon}
                        </span>
                      </div>
                      <h3 className="mt-8 text-lg font-medium text-gray-900 tracking-tight">
                        {feature.title}
                      </h3>
                      <p className="mt-5 text-base text-gray-500">
                        {feature.description}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* CTA Section */}
      <div className="bg-primary-700">
        <div className="max-w-2xl mx-auto text-center py-16 px-4 sm:py-20 sm:px-6 lg:px-8">
          <h2 className="text-3xl font-extrabold text-white sm:text-4xl">
            <span className="block">Ready to get started?</span>
            <span className="block">Sign up today.</span>
          </h2>
          <p className="mt-4 text-lg leading-6 text-primary-200">
            Generate content for your blog, social media, or website in seconds.
          </p>
          <div className="mt-8 flex justify-center">
            {user ? (
              <Link
                to="/generator"
                className="inline-flex items-center justify-center px-5 py-3 border border-transparent text-base font-medium rounded-md text-primary-700 bg-white hover:bg-primary-50"
              >
                Generate Content
              </Link>
            ) : (
              <LoginButton />
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
