import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { generateContent, reset, clearCurrentContent } from '../features/content/contentSlice';
import { FaSpinner, FaCrown, FaLightbulb, FaExclamationTriangle, FaClipboard, FaEraser, FaSave } from 'react-icons/fa';

const ContentGenerator = () => {
  const [formData, setFormData] = useState({
    prompt: '',
    contentType: 'blog',
    tone: 'professional',
    length: 'medium'
  });
  const [generatedContent, setGeneratedContent] = useState('');
  const [promptError, setPromptError] = useState(false);
  const [copied, setCopied] = useState(false);
  const [savedToLibrary, setSavedToLibrary] = useState(false);

  const dispatch = useDispatch();
  const navigate = useNavigate();
  
  const { user } = useSelector((state) => state.auth);
  const { currentContent, isLoading, isError, isSuccess, message, limitReached } = useSelector(
    (state) => state.content
  );

  useEffect(() => {
    // Redirect if user not logged in
    if (!user) {
      navigate('/');
    }

    // Clear content when component mounts
    dispatch(clearCurrentContent());

    return () => {
      dispatch(reset());
    };
  }, [user, navigate, dispatch]);

  useEffect(() => {
    if (isSuccess && currentContent) {
      setGeneratedContent(currentContent.generatedContent);
      setSavedToLibrary(true); // Content is automatically saved
    }
  }, [isSuccess, currentContent]);

  const { prompt, contentType, tone, length } = formData;

  const contentTypes = [
    { value: 'blog', label: 'Blog Post', description: 'Long-form content focused on a specific topic' },
    { value: 'article', label: 'Article', description: 'Informative and structured content with headings' },
    { value: 'social', label: 'Social Media', description: 'Short, engaging content for social platforms' },
    { value: 'seo', label: 'SEO Content', description: 'Content optimized for search engines' },
    { value: 'email', label: 'Email', description: 'Professional email content for marketing or newsletters' },
    { value: 'other', label: 'Other', description: 'Custom content type' }
  ];

  const toneOptions = [
    { value: 'professional', label: 'Professional' },
    { value: 'casual', label: 'Casual' },
    { value: 'friendly', label: 'Friendly' },
    { value: 'formal', label: 'Formal' },
    { value: 'persuasive', label: 'Persuasive' },
    { value: 'informative', label: 'Informative' }
  ];

  const lengthOptions = [
    { value: 'short', label: 'Short', description: '~250 words' },
    { value: 'medium', label: 'Medium', description: '~500 words' },
    { value: 'long', label: 'Long', description: '~1000 words' }
  ];

  const templatePrompts = {
    blog: "Write a blog post about [topic] that covers [specific aspects]. Include an engaging introduction, 3-5 main points with subheadings, and a conclusion.",
    article: "Write an article about [topic] with a focus on [angle/perspective]. Include an introduction, main sections with headings, supporting facts, and a conclusion.",
    social: "Write a social media post about [topic] that will engage followers and encourage [desired action]. Keep it concise and include relevant hashtags.",
    seo: "Write SEO-optimized content about [target keyword] covering [specific aspects]. Include headings, bullet points, and naturally incorporate related keywords.",
    email: "Write an email about [topic] for [target audience] with the goal of [objective]. Include a subject line, greeting, main content, call to action, and sign-off.",
    other: "Write content about [topic] focused on [specific goal] for [target audience]."
  };

  const onChange = (e) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value
    }));

    // Clear error when user types in prompt field
    if (e.target.name === 'prompt' && promptError) {
      setPromptError(false);
    }

    // Reset copied and saved status when form changes
    if (copied) setCopied(false);
    if (savedToLibrary) setSavedToLibrary(false);
  };

  const onSubmit = (e) => {
    e.preventDefault();

    if (!prompt.trim()) {
      setPromptError(true);
      return;
    }

    // Create enhanced prompt with tone and length information
    const enhancedFormData = {
      ...formData,
      prompt: `Generate ${length} length content in a ${tone} tone: ${prompt}`
    };

    dispatch(generateContent(enhancedFormData));
  };

  const applyTemplate = () => {
    setFormData((prevState) => ({
      ...prevState,
      prompt: templatePrompts[contentType] || templatePrompts.other
    }));
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(generatedContent);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  const clearContent = () => {
    setGeneratedContent('');
    setSavedToLibrary(false);
  };

  // Check if user data and subscription are properly loaded
  const hasSubscription = user && user.subscription;
  const subscriptionStatus = hasSubscription ? user.subscription.status : 'free';
  const usageCount = user ? user.usageCount || 0 : 0;
  const usageLimit = user ? user.usageLimit || 10 : 10;
  const usagePercentage = (usageCount / usageLimit) * 100;

  // Get current content type description
  const currentContentTypeDescription = contentTypes.find(type => type.value === contentType)?.description || '';

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-800 mb-2">AI Content Generator</h1>
        <p className="text-gray-600">
          Generate high-quality, AI-powered content for your blog, social media, or website in seconds.
        </p>
      </div>

      {/* User info and subscription reminder */}
      {user && (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4 mb-8">
          <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
            <div>
              <p className="text-sm text-gray-600">
                <span className="font-medium text-gray-800">
                  Usage: {usageCount} / {usageLimit}
                </span>{' '}
                generations this month
              </p>
              <div className="w-full bg-gray-200 rounded-full h-2 mt-2 max-w-md">
                <div
                  className={`h-2 rounded-full transition-all duration-300 ${
                    usagePercentage < 70
                      ? 'bg-green-500'
                      : usagePercentage < 90
                      ? 'bg-yellow-500'
                      : 'bg-red-500'
                  }`}
                  style={{ width: `${usagePercentage}%` }}
                ></div>
              </div>
            </div>

            {subscriptionStatus === 'free' && (
              <button
                onClick={() => navigate('/subscription')}
                className="mt-3 sm:mt-0 flex items-center text-sm bg-gradient-to-r from-secondary-600 to-secondary-700 text-white px-4 py-2 rounded-full hover:from-secondary-700 hover:to-secondary-800 transition-colors"
              >
                <FaCrown className="mr-2" /> Upgrade for Unlimited Content
              </button>
            )}
          </div>
        </div>
      )}

      {/* Error message for limit reached */}
      {limitReached && (
        <div className="bg-red-50 border-l-4 border-red-500 text-red-800 rounded-lg p-4 mb-8">
          <div className="flex">
            <div className="flex-shrink-0">
              <FaExclamationTriangle className="h-5 w-5 text-red-500" />
            </div>
            <div className="ml-3">
              <h3 className="text-lg font-semibold">Usage Limit Reached</h3>
              <p className="mt-2">
                You've reached your monthly content generation limit. Upgrade your subscription to
                generate more content.
              </p>
              <button
                onClick={() => navigate('/subscription')}
                className="mt-3 bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-colors"
              >
                View Subscription Options
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Error message for other errors */}
      {isError && !limitReached && (
        <div className="bg-red-50 border-l-4 border-red-500 text-red-800 rounded-lg p-4 mb-8">
          <div className="flex">
            <div className="flex-shrink-0">
              <FaExclamationTriangle className="h-5 w-5 text-red-500" />
            </div>
            <div className="ml-3">
              <p>{message || "An error occurred while generating content. Please try again."}</p>
            </div>
          </div>
        </div>
      )}

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Input Form */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-4">Generate Content</h2>
          <form onSubmit={onSubmit}>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-medium mb-2" htmlFor="contentType">
                Content Type
              </label>
              <select
                id="contentType"
                name="contentType"
                value={contentType}
                onChange={onChange}
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-primary-500 focus:border-primary-500"
              >
                {contentTypes.map((type) => (
                  <option key={type.value} value={type.value}>
                    {type.label}
                  </option>
                ))}
              </select>
              <p className="mt-1 text-sm text-gray-500">{currentContentTypeDescription}</p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
              <div>
                <label className="block text-gray-700 text-sm font-medium mb-2" htmlFor="tone">
                  Tone
                </label>
                <select
                  id="tone"
                  name="tone"
                  value={tone}
                  onChange={onChange}
                  className="w-full p-3 border border-gray-300 rounded-lg focus:ring-primary-500 focus:border-primary-500"
                >
                  {toneOptions.map((option) => (
                    <option key={option.value} value={option.value}>
                      {option.label}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-gray-700 text-sm font-medium mb-2" htmlFor="length">
                  Length
                </label>
                <select
                  id="length"
                  name="length"
                  value={length}
                  onChange={onChange}
                  className="w-full p-3 border border-gray-300 rounded-lg focus:ring-primary-500 focus:border-primary-500"
                >
                  {lengthOptions.map((option) => (
                    <option key={option.value} value={option.value}>
                      {option.label} ({option.description})
                    </option>
                  ))}
                </select>
              </div>
            </div>

            <div className="mb-6">
              <div className="flex justify-between items-center mb-2">
                <label className="block text-gray-700 text-sm font-medium" htmlFor="prompt">
                  Prompt
                </label>
                <button
                  type="button"
                  onClick={applyTemplate}
                  className="text-primary-600 text-sm hover:text-primary-800 flex items-center"
                >
                  <FaLightbulb className="mr-1" /> Use Template
                </button>
              </div>
              <textarea
                id="prompt"
                name="prompt"
                value={prompt}
                onChange={onChange}
                rows="5"
                placeholder="Describe what content you want to generate..."
                className={`w-full p-3 border ${
                  promptError ? 'border-red-500' : 'border-gray-300'
                } rounded-lg focus:ring-primary-500 focus:border-primary-500`}
              ></textarea>
              {promptError && (
                <p className="text-red-500 text-sm mt-1">Prompt cannot be empty</p>
              )}
              <p className="mt-1 text-sm text-gray-500">
                For best results, be specific about your topic, target audience, and desired outcome.
              </p>
            </div>

            <button
              type="submit"
              disabled={isLoading || limitReached}
              className="w-full bg-primary-600 text-white py-3 px-4 rounded-lg hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-opacity-50 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors"
            >
              {isLoading ? (
                <>
                  <FaSpinner className="inline mr-2 animate-spin" /> Generating...
                </>
              ) : (
                'Generate Content'
              )}
            </button>
          </form>
        </div>

        {/* Generated Content */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-4">Generated Content</h2>
          {generatedContent ? (
            <div className="prose max-w-none">
              <div className="whitespace-pre-wrap bg-gray-50 p-4 rounded-lg border border-gray-200 h-[400px] overflow-y-auto">
                {generatedContent}
              </div>
              <div className="mt-4 flex flex-wrap gap-2">
                <button
                  onClick={copyToClipboard}
                  className="bg-primary-100 text-primary-800 py-2 px-4 rounded-lg hover:bg-primary-200 flex items-center transition-colors"
                >
                  <FaClipboard className="mr-2" />
                  {copied ? 'Copied!' : 'Copy to Clipboard'}
                </button>
                <button
                  onClick={clearContent}
                  className="bg-gray-100 text-gray-800 py-2 px-4 rounded-lg hover:bg-gray-200 flex items-center transition-colors"
                >
                  <FaEraser className="mr-2" />
                  Clear
                </button>
                {/* Save button not needed since content is automatically saved */}
                <div className="ml-auto text-sm text-gray-500 flex items-center">
                  <FaSave className="mr-1" />
                  {savedToLibrary ? 'Saved to library' : ''}
                </div>
              </div>
            </div>
          ) : (
            <div className="flex flex-col items-center justify-center h-[400px] bg-gray-50 border border-gray-200 rounded-lg p-6 text-center">
              <FaLightbulb className="text-gray-300 text-4xl mb-4" />
              <p className="text-gray-400 mb-2">
                Your generated content will appear here
              </p>
              <p className="text-gray-400 text-sm max-w-md">
                Fill out the form on the left and click "Generate Content" to create AI-powered content.
              </p>
            </div>
          )}
        </div>
      </div>

      {/* Tips Section */}
      <div className="mt-8 bg-primary-50 rounded-lg p-6">
        <h2 className="text-xl font-semibold text-gray-800 mb-4">Tips for Better Results</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="bg-white p-4 rounded-lg shadow-sm">
            <h3 className="font-medium text-primary-700 mb-2">Be Specific</h3>
            <p className="text-sm text-gray-600">
              The more specific your prompt, the better the results. Include details about your topic, audience, and desired outcome.
            </p>
          </div>
          <div className="bg-white p-4 rounded-lg shadow-sm">
            <h3 className="font-medium text-primary-700 mb-2">Structure Your Request</h3>
            <p className="text-sm text-gray-600">
              Ask for specific sections, headings, or elements you want in your content. This helps the AI organize information better.
            </p>
          </div>
          <div className="bg-white p-4 rounded-lg shadow-sm">
            <h3 className="font-medium text-primary-700 mb-2">Iterate and Refine</h3>
            <p className="text-sm text-gray-600">
              If the results aren't perfect, try refining your prompt with more specific instructions or a different approach.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ContentGenerator;
