import React, { useState } from 'react';
import { FcGoogle } from 'react-icons/fc';

const LoginButton = () => {
  const [error, setError] = useState(null);

  const handleGoogleLogin = () => {
    try {
      console.log('Initiating Google OAuth flow...');
      window.location.href = 'http://localhost:5000/api/auth/google';
    } catch (err) {
      console.error('Error during Google login redirect:', err);
      setError('Failed to connect to authentication service');
    }
  };

  return (
    <div>
      <button
        onClick={handleGoogleLogin}
        className="flex items-center justify-center gap-2 bg-white text-gray-700 font-semibold py-2 px-4 border border-gray-300 rounded-lg shadow-sm hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-opacity-50 transition-colors"
      >
        <FcGoogle className="text-xl" />
        <span>Sign in with Google</span>
      </button>
      
      {error && (
        <div className="mt-2 text-red-600 text-sm">
          {error}
        </div>
      )}
      
      <div className="mt-2 text-xs text-gray-500">
        Note: Make sure pop-ups are enabled for this site
      </div>
    </div>
  );
};

export default LoginButton;
