import React from 'react';
import { FaCheck } from 'react-icons/fa';

const SubscriptionPlan = ({ plan, isCurrentPlan, onSelect }) => {
  const { name, price, features } = plan;

  // Determine if this is a paid plan
  const isPaidPlan = price > 0;

  return (
    <div
      className={`border rounded-lg p-6 flex flex-col ${
        isCurrentPlan
          ? 'border-2 border-primary-500 bg-primary-50'
          : 'border-gray-200 hover:border-primary-300 bg-white'
      }`}
    >
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-xl font-semibold text-gray-800">{name}</h3>
        {isCurrentPlan && (
          <span className="bg-primary-100 text-primary-800 text-xs font-semibold px-2.5 py-0.5 rounded">
            Current Plan
          </span>
        )}
      </div>

      <div className="mb-6">
        <div className="flex items-baseline">
          <span className="text-3xl font-extrabold text-gray-900">${price}</span>
          {isPaidPlan && <span className="text-gray-500 text-sm ml-1">/month</span>}
        </div>
      </div>

      <ul className="mb-8 space-y-3 flex-1">
        {features.map((feature, index) => (
          <li key={index} className="flex items-start">
            <FaCheck className="text-green-500 mr-2 mt-1 flex-shrink-0" />
            <span className="text-gray-600">{feature}</span>
          </li>
        ))}
      </ul>

      <button
        onClick={() => onSelect(plan)}
        disabled={isCurrentPlan}
        className={`w-full py-2 px-4 rounded-lg font-medium ${
          isCurrentPlan
            ? 'bg-gray-200 text-gray-500 cursor-not-allowed'
            : isPaidPlan
            ? 'bg-primary-600 text-white hover:bg-primary-700'
            : 'bg-secondary-600 text-white hover:bg-secondary-700'
        }`}
      >
        {isCurrentPlan ? 'Current Plan' : isPaidPlan ? 'Upgrade' : 'Select Plan'}
      </button>
    </div>
  );
};

export default SubscriptionPlan;
