import React from 'react';
import { useNavigate } from 'react-router-dom';
import { FaTimesCircle } from 'react-icons/fa';

const PaymentCancel = () => {
  const navigate = useNavigate();
  
  return (
    <div className="max-w-4xl mx-auto px-4 py-16 text-center">
      <div className="bg-orange-50 border border-orange-200 text-orange-800 rounded-lg p-8 inline-block">
        <FaTimesCircle className="text-5xl text-orange-500 mx-auto mb-6" />
        <h1 className="text-3xl font-bold mb-4">Payment Cancelled</h1>
        <p className="text-xl mb-6">
          Your payment has been cancelled and no charges were made.
        </p>
        <p className="mb-8">
          You can try again whenever you're ready, or contact our support team if you need assistance.
        </p>
        <div className="flex flex-col sm:flex-row justify-center gap-4">
          <button
            onClick={() => navigate('/subscription')}
            className="bg-primary-600 text-white py-3 px-8 rounded-lg hover:bg-primary-700"
          >
            Try Again
          </button>
          <button
            onClick={() => navigate('/dashboard')}
            className="bg-gray-200 text-gray-800 py-3 px-8 rounded-lg hover:bg-gray-300"
          >
            Go to Dashboard
          </button>
        </div>
      </div>
    </div>
  );
};

export default PaymentCancel;
