import React from 'react';
import { useNavigate } from 'react-router-dom';
import { FaTimesCircle } from 'react-icons/fa';

const PaymentCancelled = () => {
  const navigate = useNavigate();

  return (
    <div className="max-w-4xl mx-auto px-4 py-16 text-center">
      <div className="bg-gray-50 border border-gray-200 rounded-lg p-8 inline-block">
        <FaTimesCircle className="text-5xl text-gray-500 mx-auto mb-6" />
        <h1 className="text-3xl font-bold text-gray-800 mb-4">Payment Cancelled</h1>
        <p className="text-gray-600 mb-6">
          Your payment process has been cancelled. No charges have been made to your account.
        </p>
        <div className="space-y-4">
          <button
            onClick={() => navigate('/subscription')}
            className="bg-primary-600 text-white py-3 px-8 rounded-lg hover:bg-primary-700 w-full md:w-auto"
          >
            Return to Subscription Plans
          </button>
          <button
            onClick={() => navigate('/dashboard')}
            className="text-gray-600 py-3 px-8 rounded-lg border border-gray-300 hover:bg-gray-50 w-full md:w-auto"
          >
            Go to Dashboard
          </button>
        </div>
      </div>
    </div>
  );
};

export default PaymentCancelled;
