import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { getSubscriptionPlans, getUserSubscription, reset } from '../features/subscription/subscriptionSlice';
import SubscriptionPlan from '../components/SubscriptionPlan';
import PayPalButton from '../components/PayPalButton';
import { FaSpinner, FaCheckCircle, FaInfoCircle, FaCrown, FaArrowLeft } from 'react-icons/fa';

const SubscriptionPage = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [selectedPlan, setSelectedPlan] = useState(null);

  const { user } = useSelector((state) => state.auth);
  const { plans, userSubscription, isLoading, isError, message } = useSelector(
    (state) => state.subscription
  );

  useEffect(() => {
    // Redirect if user not logged in
    if (!user) {
      navigate('/');
    }

    // Get subscription plans
    dispatch(getSubscriptionPlans());

    // Get user subscription details
    dispatch(getUserSubscription());

    return () => {
      dispatch(reset());
    };
  }, [user, navigate, dispatch]);

  const handleSelectPlan = (plan) => {
    // Free plan doesn't need to go through checkout
    if (plan.id === 'free') {
      // This would normally handle downgrading to free plan
      // In a real application, this would call an API to update the user's plan
      alert('You have selected the Free plan. This would normally downgrade your subscription.');
      return;
    }

    // For paid plans, set the selected plan
    setSelectedPlan(plan);
    // Scroll to the checkout section
    setTimeout(() => {
      document.getElementById('checkout-section')?.scrollIntoView({ behavior: 'smooth' });
    }, 100);
  };

  const handlePaymentSuccess = () => {
    // Refresh user subscription details
    dispatch(getUserSubscription());
    
    // Clear selected plan
    setSelectedPlan(null);
    
    // Show success message (removed alert as PayPalButton now shows success UI)
  };

  // Safely extract user and subscription data with fallbacks
  const usageLimit = user?.usageLimit || 10;
  const usageCount = user?.usageCount || 0;
  const usagePercentage = Math.min(Math.round((usageCount / usageLimit) * 100), 100);
  const subscriptionStatus = userSubscription?.status || 'free';
  const daysRemaining = userSubscription?.daysRemaining;

  if (isLoading) {
    return (
      <div className="max-w-6xl mx-auto px-4 py-16 flex justify-center">
        <FaSpinner className="text-primary-500 text-3xl animate-spin" />
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <div className="mb-12 text-center">
        <h1 className="text-3xl font-bold text-gray-800 mb-4">Subscription Plans</h1>
        <p className="text-lg text-gray-600 max-w-3xl mx-auto">
          Choose the plan that works best for you and start generating content with AI.
        </p>
      </div>

      {/* Error message */}
      {isError && (
        <div className="bg-red-50 border border-red-200 text-red-800 rounded-lg p-4 mb-8 flex items-start">
          <FaInfoCircle className="text-red-500 mt-1 mr-2 flex-shrink-0" />
          <p>{message}</p>
        </div>
      )}

      {/* Current subscription info */}
      {userSubscription && (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-12">
          <div className="flex flex-col md:flex-row md:items-center md:justify-between">
            <div>
              <h2 className="text-xl font-semibold text-gray-800 mb-2">Your Current Subscription</h2>
              <div className="flex items-center gap-2 mb-4">
                <span
                  className={`inline-flex items-center rounded-full px-3 py-1 text-xs font-medium ${
                    subscriptionStatus === 'free'
                      ? 'bg-gray-100 text-gray-800'
                      : subscriptionStatus === 'basic'
                      ? 'bg-primary-100 text-primary-800'
                      : 'bg-secondary-100 text-secondary-800'
                  }`}
                >
                  <FaCheckCircle className="mr-1" />
                  {subscriptionStatus.charAt(0).toUpperCase() + subscriptionStatus.slice(1)} Plan
                </span>
                {daysRemaining !== null && daysRemaining !== undefined && (
                  <span className="text-sm text-gray-500">
                    {daysRemaining} days remaining
                  </span>
                )}
              </div>
              <div className="mb-4">
                <div className="flex items-center gap-2 mb-1">
                  <FaInfoCircle className="text-gray-400" />
                  <span className="text-gray-600 text-sm">
                    Usage: <span className="font-semibold">{usageCount} / {usageLimit}</span> content pieces
                  </span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2.5">
                  <div 
                    className={`h-2.5 rounded-full ${
                      usagePercentage > 90 ? 'bg-red-500' : 
                      usagePercentage > 70 ? 'bg-yellow-500' : 
                      'bg-green-500'
                    }`} 
                    style={{ width: `${usagePercentage}%` }}
                  ></div>
                </div>
              </div>
            </div>
            <div className="mt-4 md:mt-0">
              <button
                onClick={() => navigate('/generator')}
                className="px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 transition-colors"
              >
                Go to Content Generator
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Selected plan checkout */}
      {selectedPlan && (
        <div id="checkout-section" className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-8">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-semibold text-gray-800">
              <FaCrown className="inline-block text-yellow-500 mr-2" />
              Upgrade to {selectedPlan.name} Plan
            </h2>
            <button
              onClick={() => setSelectedPlan(null)}
              className="text-gray-500 hover:text-gray-700"
            >
              <FaArrowLeft className="mr-1 inline-block" /> Back to plans
            </button>
          </div>
          <div className="flex flex-col md:flex-row gap-8">
            <div className="md:w-2/3">
              <p className="text-gray-600 mb-4">
                You're about to upgrade to the {selectedPlan.name} Plan for ${selectedPlan.price}/month.
              </p>
              <ul className="space-y-2 mb-6">
                {selectedPlan.features.map((feature, index) => (
                  <li key={index} className="flex items-start">
                    <FaCheckCircle className="text-green-500 mr-2 mt-1 flex-shrink-0" />
                    <span className="text-gray-600">{feature}</span>
                  </li>
                ))}
              </ul>
              <div className="border rounded-lg overflow-hidden">
                <PayPalButton 
                  planId={selectedPlan.id} 
                  onSuccess={handlePaymentSuccess} 
                />
              </div>
            </div>
            <div className="md:w-1/3 bg-gray-50 p-4 rounded-lg">
              <h3 className="font-medium text-gray-800 mb-2">Order Summary</h3>
              <div className="flex justify-between py-2 border-b border-gray-200">
                <span>{selectedPlan.name} Plan</span>
                <span>${selectedPlan.price}/month</span>
              </div>
              <div className="flex justify-between py-2 font-semibold">
                <span>Total</span>
                <span>${selectedPlan.price}/month</span>
              </div>
              <p className="text-xs text-gray-500 mt-4">
                Your subscription will automatically renew each month. You can cancel anytime.
              </p>
            </div>
          </div>
        </div>
      )}

      {/* Subscription plans */}
      {!selectedPlan && plans && plans.length > 0 && (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {plans.map((plan) => (
            <SubscriptionPlan
              key={plan.id}
              plan={plan}
              isCurrentPlan={userSubscription?.status === plan.id}
              onSelect={handleSelectPlan}
            />
          ))}
        </div>
      )}

      <div className="mt-12 bg-gray-50 rounded-lg p-6 border border-gray-200">
        <h3 className="text-lg font-semibold text-gray-800 mb-4">About Subscriptions</h3>
        <ul className="space-y-2 text-gray-600">
          <li className="flex items-start">
            <FaCheckCircle className="text-green-500 mr-2 mt-1 flex-shrink-0" />
            <span>
              All plans include access to the AI content generator using Google Gemini.
            </span>
          </li>
          <li className="flex items-start">
            <FaCheckCircle className="text-green-500 mr-2 mt-1 flex-shrink-0" />
            <span>
              Subscriptions are processed securely through PayPal and will automatically renew.
            </span>
          </li>
          <li className="flex items-start">
            <FaCheckCircle className="text-green-500 mr-2 mt-1 flex-shrink-0" />
            <span>
              You can cancel your subscription at any time through your account settings.
            </span>
          </li>
          <li className="flex items-start">
            <FaCheckCircle className="text-green-500 mr-2 mt-1 flex-shrink-0" />
            <span>
              If you have any questions about billing or subscriptions, please contact our support team.
            </span>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default SubscriptionPage;
