import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { getCurrentUser } from '../features/auth/authSlice';
import Dashboard from '../components/Dashboard';
import Spinner from '../components/Spinner';

function DashboardPage() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  
  const { user, isLoading, isError, message } = useSelector((state) => state.auth);
  const contentState = useSelector((state) => state.content);

  // Add debug logging
  console.log('Dashboard Page Rendering');
  console.log('Auth State:', { user, isLoading, isError, message });
  console.log('Content State:', contentState);
  
  useEffect(() => {
    // Redirect if not logged in
    if (!user) {
      console.log('No user found, redirecting to home');
      navigate('/');
    } else {
      console.log('User found, dispatching getCurrentUser');
      dispatch(getCurrentUser());
    }
  }, [user, navigate, dispatch]);

  // Render spinner while loading
  if (isLoading) {
    console.log('Loading state active');
    return (
      <div className="container mx-auto px-4 py-8">
        <Spinner />
      </div>
    );
  }

  // Render error state if error
  if (isError) {
    console.error('Error in dashboard:', message);
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="bg-red-50 border border-red-200 rounded-lg p-6 text-center">
          <h2 className="text-xl font-semibold text-red-800 mb-2">Something went wrong</h2>
          <p className="text-red-700 mb-4">{message}</p>
          <button
            onClick={() => dispatch(getCurrentUser())}
            className="bg-red-600 text-white py-2 px-4 rounded-lg hover:bg-red-700"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  // If no errors and not loading, render dashboard
  console.log('Rendering Dashboard component');
  return (
    <div className="container mx-auto px-4 py-8">
      <Dashboard />
    </div>
  );
}

export default DashboardPage;
