import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getContents } from '../features/content/contentSlice';
import { getCurrentUser } from '../features/auth/authSlice';
import { FaClipboard, FaNewspaper, FaTwitter, FaMailBulk, FaSearch, FaEllipsisH, FaCrown, FaCalendarDay, FaExclamationTriangle } from 'react-icons/fa';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  const dispatch = useDispatch();
  const [contentLoaded, setContentLoaded] = useState(false);

  // Get user and content data from Redux store with fallback values
  const { user, isLoading: userLoading } = useSelector((state) => state.auth || {});
  const { contents = [], isLoading: contentLoading, isError, message } = useSelector((state) => state.content || {});

  // Use useEffect to fetch user data if not already loaded
  useEffect(() => {
    if (user && user.token && !user.name) {
      dispatch(getCurrentUser());
    }
  }, [user, dispatch]);

  // Use useEffect to fetch content when user is available
  useEffect(() => {
    if (user && user.token && !contentLoaded) {
      dispatch(getContents({ page: 1, limit: 5 }))
        .unwrap()
        .then(() => setContentLoaded(true))
        .catch((error) => console.error('Error fetching content:', error));
    }
  }, [user, dispatch, contentLoaded]);

  // Show loading spinner while user data is loading
  if (userLoading) {
    return (
      <div className="flex justify-center items-center p-12">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
        <span className="ml-3 text-gray-600">Loading dashboard...</span>
      </div>
    );
  }

  // Early return with message if no user
  if (!user || !user.token) {
    return (
      <div className="text-center py-10">
        <FaExclamationTriangle className="mx-auto h-12 w-12 text-yellow-500 mb-4" />
        <h2 className="text-2xl font-bold text-gray-800 mb-2">Authentication Required</h2>
        <p className="text-gray-600 mb-6">Please log in to access your dashboard</p>
        <Link 
          to="/" 
          className="bg-primary-600 text-white py-2 px-6 rounded-lg hover:bg-primary-700 transition-colors"
        >
          Go to Home Page
        </Link>
      </div>
    );
  }

  // Safely destructure user properties with fallback values
  const { 
    name = 'User', 
    email = '', 
    subscription = { currentPlan: 'free', daysRemaining: null },
    usageCount = 0,
    usageLimit = 10
  } = user;

  // Derived values with safety checks
  const currentPlan = subscription?.currentPlan || 'free';
  const daysRemaining = subscription?.daysRemaining;
  const usagePercentage = usageLimit > 0 ? Math.min(Math.round((usageCount / usageLimit) * 100), 100) : 0;

  // Content type icons
  const contentTypeIcons = {
    blog: <FaNewspaper className="h-4 w-4" />,
    article: <FaClipboard className="h-4 w-4" />,
    social: <FaTwitter className="h-4 w-4" />,
    email: <FaMailBulk className="h-4 w-4" />,
    seo: <FaSearch className="h-4 w-4" />,
    other: <FaEllipsisH className="h-4 w-4" />
  };

  return (
    <div className="flex flex-col w-full max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
      {/* Welcome Card */}
      <div className="bg-white rounded-lg shadow p-6 mb-6">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center">
          <div>
            <h1 className="text-2xl font-bold text-gray-800">Welcome, {name}</h1>
            <p className="text-gray-600 mt-1">{email}</p>
          </div>

          <div className="mt-4 sm:mt-0">
            <div className="flex items-center bg-primary-50 text-primary-700 px-4 py-2 rounded-full">
              <FaCrown className="mr-2" />
              <span>
                {currentPlan.charAt(0).toUpperCase() + currentPlan.slice(1)} Plan
                {daysRemaining && (
                  <span className="ml-2 text-xs px-2 py-1 bg-primary-100 rounded-full">
                    <FaCalendarDay className="inline mr-1" /> {daysRemaining} days left
                  </span>
                )}
              </span>
            </div>
          </div>
        </div>
      </div>

      {/* Error message if content loading failed */}
      {isError && (
        <div className="bg-red-50 border-l-4 border-red-500 p-4 mb-6">
          <div className="flex">
            <div className="flex-shrink-0">
              <FaExclamationTriangle className="h-5 w-5 text-red-500" />
            </div>
            <div className="ml-3">
              <p className="text-sm text-red-700">
                {message || "There was an error loading your content. Please try refreshing the page."}
              </p>
            </div>
          </div>
        </div>
      )}

      {/* Usage and Content Generation */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-6">
        <div className="bg-white rounded-lg shadow p-6 lg:col-span-1">
          <h2 className="text-xl font-semibold text-gray-800 mb-4">Your Usage</h2>

          <div className="mb-4">
            <div className="flex justify-between mb-1">
              <span className="text-gray-700">
                {usageCount} of {usageLimit} generations used
              </span>
              <span className="text-gray-700 font-medium">{usagePercentage}%</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div
                className="h-2 rounded-full transition-all duration-500"
                style={{
                  width: `${usagePercentage}%`,
                  backgroundColor: usagePercentage < 70 ? '#10B981' : usagePercentage < 90 ? '#F59E0B' : '#EF4444'
                }}
              ></div>
            </div>
          </div>

          <Link
            to="/generator"
            className="block w-full bg-primary-600 text-white text-center py-2 px-4 rounded-lg hover:bg-primary-700 transition-colors"
          >
            Create New Content
          </Link>

          {currentPlan === 'free' && (
            <Link
              to="/subscription"
              className="block w-full text-center border border-primary-600 text-primary-600 py-2 px-4 rounded-lg mt-2 hover:bg-primary-50 transition-colors"
            >
              Upgrade Plan
            </Link>
          )}
        </div>

        <div className="bg-white rounded-lg shadow p-6 lg:col-span-2">
          <h2 className="text-xl font-semibold text-gray-800 mb-4">Recent Content</h2>

          {contentLoading ? (
            <div className="text-center py-8">
              <div className="animate-spin rounded-full h-10 w-10 border-b-2 border-primary-600 mx-auto"></div>
              <p className="mt-2 text-gray-600">Loading your content...</p>
            </div>
          ) : contents && contents.length > 0 ? (
            <div className="divide-y divide-gray-200">
              {contents.slice(0, 3).map((content) => (
                <div key={content._id} className="py-4">
                  <div className="flex items-center mb-2">
                    <span className="inline-flex items-center justify-center p-1 rounded bg-gray-100 text-gray-500 mr-2">
                      {contentTypeIcons[content.contentType] || contentTypeIcons.other}
                    </span>
                    <span className="text-sm text-gray-500">
                      {new Date(content.createdAt).toLocaleDateString('en-US', {
                        month: 'short',
                        day: 'numeric',
                        year: 'numeric'
                      })}
                    </span>
                  </div>

                  <p className="text-gray-800 font-medium mb-1 truncate">{content.prompt}</p>
                  <p className="text-gray-600 text-sm line-clamp-2">
                    {content.generatedContent ? content.generatedContent.substring(0, 120) + '...' : 'No content available'}
                  </p>

                  <Link 
                    to={`/content/${content._id}`} 
                    className="text-primary-600 text-sm hover:underline mt-2 inline-block"
                  >
                    View Full Content
                  </Link>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8">
              <p className="text-gray-600 mb-4">You haven't generated any content yet.</p>
              <Link
                to="/generator"
                className="inline-block bg-primary-600 text-white py-2 px-6 rounded-lg hover:bg-primary-700 transition-colors"
              >
                Create Your First Content
              </Link>
            </div>
          )}

          {contents && contents.length > 0 && (
            <Link
              to="/content"
              className="block text-center text-primary-600 mt-4 hover:underline"
            >
              View All Content
            </Link>
          )}
        </div>
      </div>

      {/* Quick Actions Card */}
      <div className="bg-white rounded-lg shadow p-6">
        <h2 className="text-xl font-semibold text-gray-800 mb-4">Quick Actions</h2>

        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4">
          <Link 
            to="/generator" 
            className="flex flex-col items-center p-4 bg-primary-50 rounded-lg hover:bg-primary-100 transition-colors"
          >
            <FaClipboard className="h-8 w-8 text-primary-600 mb-2" />
            <span className="text-gray-800 font-medium">New Content</span>
          </Link>

          <Link 
            to="/content" 
            className="flex flex-col items-center p-4 bg-green-50 rounded-lg hover:bg-green-100 transition-colors"
          >
            <FaNewspaper className="h-8 w-8 text-green-600 mb-2" />
            <span className="text-gray-800 font-medium">My Content</span>
          </Link>

          <Link 
            to="/subscription" 
            className="flex flex-col items-center p-4 bg-purple-50 rounded-lg hover:bg-purple-100 transition-colors"
          >
            <FaCrown className="h-8 w-8 text-purple-600 mb-2" />
            <span className="text-gray-800 font-medium">Subscription</span>
          </Link>

          <Link 
            to="/settings" 
            className="flex flex-col items-center p-4 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors"
          >
            <FaEllipsisH className="h-8 w-8 text-gray-600 mb-2" />
            <span className="text-gray-800 font-medium">Settings</span>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
