import React, { useState } from 'react';
import axios from 'axios';
import { FaSpinner } from 'react-icons/fa';

const GeminiAI = ({ onContentGenerated }) => {
  const [prompt, setPrompt] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [contentType, setContentType] = useState('blog');

  const contentTypes = [
    { value: 'blog', label: 'Blog Post' },
    { value: 'article', label: 'Article' },
    { value: 'social', label: 'Social Media' },
    { value: 'seo', label: 'SEO Content' },
    { value: 'email', label: 'Email' },
    { value: 'other', label: 'Other' }
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!prompt.trim()) {
      setError('Please enter a prompt');
      return;
    }

    try {
      setLoading(true);
      setError(null);
      
      // Get user token from localStorage
      const user = JSON.parse(localStorage.getItem('user'));
      
      if (!user || !user.token) {
        throw new Error('You need to be logged in to generate content');
      }
      
      // Make API request to backend
      const response = await axios.post(
        '/api/content/generate',
        {
          prompt,
          contentType
        },
        {
          headers: {
            'Content-Type': 'application/json',
            'x-auth-token': user.token
          }
        }
      );
      
      // Handle the response
      if (response.data && response.data.generatedContent) {
        if (onContentGenerated) {
          onContentGenerated(response.data.generatedContent);
        }
        setPrompt('');
      } else {
        throw new Error('No content was generated');
      }
    } catch (err) {
      console.error('Error generating content:', err);
      
      // Check if it's a usage limit error
      if (err.response && err.response.data && err.response.data.limitReached) {
        setError('You have reached your usage limit. Please upgrade your subscription plan.');
      } else if (err.response && err.response.data && err.response.data.msg) {
        setError(err.response.data.msg);
      } else {
        setError('Failed to generate content. Please try again later.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <h2 className="text-xl font-bold text-gray-800 mb-4">Google Gemini AI Content Generator</h2>
      
      {error && (
        <div className="bg-red-50 border border-red-200 text-red-800 rounded-lg p-3 mb-4">
          {error}
        </div>
      )}
      
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="contentType" className="block text-sm font-medium text-gray-700 mb-1">
            Content Type
          </label>
          <select
            id="contentType"
            value={contentType}
            onChange={(e) => setContentType(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-md focus:ring-primary-500 focus:border-primary-500"
          >
            {contentTypes.map(type => (
              <option key={type.value} value={type.value}>
                {type.label}
              </option>
            ))}
          </select>
        </div>
        
        <div className="mb-4">
          <label htmlFor="prompt" className="block text-sm font-medium text-gray-700 mb-1">
            Prompt
          </label>
          <textarea
            id="prompt"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Describe what content you want to generate..."
            className="w-full p-2 border border-gray-300 rounded-md focus:ring-primary-500 focus:border-primary-500"
            rows="5"
            required
          ></textarea>
        </div>
        
        <button
          type="submit"
          disabled={loading}
          className="w-full bg-primary-600 text-white py-2 px-4 rounded-md hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-opacity-50 disabled:bg-gray-400"
        >
          {loading ? (
            <>
              <FaSpinner className="inline animate-spin mr-2" />
              Generating...
            </>
          ) : (
            'Generate Content'
          )}
        </button>
      </form>
    </div>
  );
};

export default GeminiAI;
