import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { generateContent, reset, clearCurrentContent } from '../features/content/contentSlice';
import { FaSpinner, FaCrown } from 'react-icons/fa';

const ContentGenerator = () => {
  const [formData, setFormData] = useState({
    prompt: '',
    contentType: 'blog'
  });
  const [generatedContent, setGeneratedContent] = useState('');
  const [promptError, setPromptError] = useState(false);

  const dispatch = useDispatch();
  const navigate = useNavigate();
  
  const { user } = useSelector((state) => state.auth);
  const { currentContent, isLoading, isError, isSuccess, message, limitReached } = useSelector(
    (state) => state.content
  );

  useEffect(() => {
    // Redirect if user not logged in
    if (!user) {
      navigate('/');
    }

    // Clear content when component mounts
    dispatch(clearCurrentContent());

    return () => {
      dispatch(reset());
    };
  }, [user, navigate, dispatch]);

  useEffect(() => {
    if (isSuccess && currentContent) {
      setGeneratedContent(currentContent.generatedContent);
    }
  }, [isSuccess, currentContent]);

  const { prompt, contentType } = formData;

  const contentTypes = [
    { value: 'blog', label: 'Blog Post' },
    { value: 'article', label: 'Article' },
    { value: 'social', label: 'Social Media' },
    { value: 'seo', label: 'SEO Content' },
    { value: 'email', label: 'Email' },
    { value: 'other', label: 'Other' }
  ];

  const onChange = (e) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value
    }));

    // Clear error when user types in prompt field
    if (e.target.name === 'prompt' && promptError) {
      setPromptError(false);
    }
  };

  const onSubmit = (e) => {
    e.preventDefault();

    if (!prompt.trim()) {
      setPromptError(true);
      return;
    }

    dispatch(generateContent(formData));
  };

  // Check if user data and subscription are properly loaded
  const hasSubscription = user && user.subscription;
  const subscriptionStatus = hasSubscription ? user.subscription.status : 'free';
  const usageCount = user ? user.usageCount || 0 : 0;
  const usageLimit = user ? user.usageLimit || 10 : 10;
  const usagePercentage = (usageCount / usageLimit) * 100;

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-800 mb-2">Content Generator</h1>
        <p className="text-gray-600">
          Generate AI-powered content for your blog, social media, or website.
        </p>
      </div>

      {/* User info and subscription reminder */}
      {user && (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4 mb-8">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600">
                <span className="font-medium text-gray-800">
                  Usage: {usageCount} / {usageLimit}
                </span>{' '}
                generations this month
              </p>
              <div className="w-full bg-gray-200 rounded-full h-2 mt-2 max-w-md">
                <div
                  className={`h-2 rounded-full ${
                    usagePercentage < 70
                      ? 'bg-green-500'
                      : usagePercentage < 90
                      ? 'bg-yellow-500'
                      : 'bg-red-500'
                  }`}
                  style={{ width: `${usagePercentage}%` }}
                ></div>
              </div>
            </div>

            {subscriptionStatus === 'free' && (
              <button
                onClick={() => navigate('/subscription')}
                className="flex items-center text-sm bg-secondary-100 text-secondary-800 px-3 py-1 rounded-full hover:bg-secondary-200"
              >
                <FaCrown className="mr-1" /> Upgrade
              </button>
            )}
          </div>
        </div>
      )}

      {/* Error message for limit reached */}
      {limitReached && (
        <div className="bg-red-50 border border-red-200 text-red-800 rounded-lg p-4 mb-8">
          <h3 className="text-lg font-semibold mb-2">Usage Limit Reached</h3>
          <p className="mb-4">
            You've reached your monthly content generation limit. Upgrade your subscription to
            generate more content.
          </p>
          <button
            onClick={() => navigate('/subscription')}
            className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700"
          >
            Upgrade Subscription
          </button>
        </div>
      )}

      {/* Error message for other errors */}
      {isError && !limitReached && (
        <div className="bg-red-50 border border-red-200 text-red-800 rounded-lg p-4 mb-8">
          <p>{message}</p>
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        {/* Input Form */}
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-4">Generate Content</h2>
          <form onSubmit={onSubmit}>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-medium mb-2" htmlFor="contentType">
                Content Type
              </label>
              <select
                id="contentType"
                name="contentType"
                value={contentType}
                onChange={onChange}
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-primary-500 focus:border-primary-500"
              >
                {contentTypes.map((type) => (
                  <option key={type.value} value={type.value}>
                    {type.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="mb-6">
              <label className="block text-gray-700 text-sm font-medium mb-2" htmlFor="prompt">
                Prompt
              </label>
              <textarea
                id="prompt"
                name="prompt"
                value={prompt}
                onChange={onChange}
                rows="5"
                placeholder="Describe what content you want to generate..."
                className={`w-full p-3 border ${
                  promptError ? 'border-red-500' : 'border-gray-300'
                } rounded-lg focus:ring-primary-500 focus:border-primary-500`}
              ></textarea>
              {promptError && (
                <p className="text-red-500 text-sm mt-1">Prompt cannot be empty</p>
              )}
            </div>

            <button
              type="submit"
              disabled={isLoading}
              className="w-full bg-primary-600 text-white py-3 px-4 rounded-lg hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-opacity-50 disabled:bg-gray-400 disabled:cursor-not-allowed"
            >
              {isLoading ? (
                <>
                  <FaSpinner className="inline mr-2 animate-spin" /> Generating...
                </>
              ) : (
                'Generate Content'
              )}
            </button>
          </form>
        </div>

        {/* Generated Content */}
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-4">Generated Content</h2>
          {generatedContent ? (
            <div className="prose max-w-none">
              <div className="whitespace-pre-wrap bg-gray-50 p-4 rounded-lg border border-gray-200 h-[400px] overflow-y-auto">
                {generatedContent}
              </div>
              <div className="mt-4 flex gap-2">
                <button
                  onClick={() => {
                    navigator.clipboard.writeText(generatedContent);
                  }}
                  className="bg-primary-100 text-primary-800 py-2 px-4 rounded-lg hover:bg-primary-200"
                >
                  Copy to Clipboard
                </button>
                <button
                  onClick={() => setGeneratedContent('')}
                  className="bg-gray-100 text-gray-800 py-2 px-4 rounded-lg hover:bg-gray-200"
                >
                  Clear
                </button>
              </div>
            </div>
          ) : (
            <div className="flex items-center justify-center h-[400px] bg-gray-50 border border-gray-200 rounded-lg">
              <p className="text-gray-400">
                Generated content will appear here
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ContentGenerator;
